function queryJson(obj, key, val) {
    if (typeof val == 'undefined')
        return obj;
    var objects = [];
    for (var i in obj) {
        if (!obj.hasOwnProperty(i)) continue;
        if (typeof obj[i] == 'object') {
            objects = objects.concat(queryJson(obj[i], key, val));
        }
        else if (i == key && obj[key] == val) {
            objects.push(obj);
        }
    }
    return objects;
}

