    parse: function ($element) {
      var params = {};
      for (var prop in $element.data()) {
        if (prop.indexOf("omni") == 0) {
          var name = this.variables[prop.toLowerCase()];
          if (!name) {
            console.error(
              prop.toLowerCase() + " was not defined in the variables."
            );
          } else {
            params[name] = $element.data(prop);
          }
        }
      }
      return params;
    },
